

module AbsBeatle where

-- Haskell module generated by the BNF converter




newtype TIdent = TIdent String deriving (Eq, Ord, Show, Read)
newtype TPolyIdent = TPolyIdent String
  deriving (Eq, Ord, Show, Read)
newtype VIdent = VIdent String deriving (Eq, Ord, Show, Read)
data Program = Prog [Phrase]
  deriving (Eq, Ord, Show, Read)

data Phrase = Value LetDef | Expression Expr | TypeDecl TypeDef
  deriving (Eq, Ord, Show, Read)

data LetDef = Let [LetBind] | LetRec [LetBind]
  deriving (Eq, Ord, Show, Read)

data LetBind
    = ConstBind Pattern Expr | ProcBind ProcName [Pattern] RType Expr
  deriving (Eq, Ord, Show, Read)

data PNested = PAlgWild | PAlgList [Pattern]
  deriving (Eq, Ord, Show, Read)

data CasePat
    = CPattern Pattern
    | CTypeAlgRec TIdent PNested
    | CNamedPat VIdent Pattern
    | CListCons Pattern Pattern
  deriving (Eq, Ord, Show, Read)

data Pattern
    = PId VIdent
    | PInt Integer
    | PTrue
    | PFalse
    | PWildcard
    | PListEmpty
    | PTypeAlg TIdent
    | PTyped Pattern Type
    | PList [Pattern]
    | PTypeAlgRec TIdent PNested
    | PNamedPat VIdent Pattern
    | PListCons Pattern Pattern
  deriving (Eq, Ord, Show, Read)

data Expr
    = EId VIdent
    | EInt Integer
    | ETrue
    | EFalse
    | EListEmpty
    | ETypeAlg TIdent
    | EApp Expr Expr
    | ETyped Expr Type
    | ENeg Expr
    | ENot Expr
    | EMul Expr Expr
    | EDiv Expr Expr
    | EMod Expr Expr
    | EAdd Expr Expr
    | ESub Expr Expr
    | EListCons Expr Expr
    | ELTH Expr Expr
    | ELE Expr Expr
    | EGTH Expr Expr
    | EGE Expr Expr
    | EEQU Expr Expr
    | ENE Expr Expr
    | EAnd Expr Expr
    | EOr Expr Expr
    | ECond Expr Expr Expr
    | ELetIn LetDef Expr
    | EMatch VIdent [Matching]
    | ELambda [VIdent] Expr
    | EList [Expr]
    | ETypeCons TIdent [Expr]
  deriving (Eq, Ord, Show, Read)

data Matching = MatchCase CasePat Expr
  deriving (Eq, Ord, Show, Read)

data ProcName = ProcNameId VIdent
  deriving (Eq, Ord, Show, Read)

data TypeDef = TDef TIdent [TPolyIdent] [TypeCons]
  deriving (Eq, Ord, Show, Read)

data TypeCons = TCons TIdent [Type]
  deriving (Eq, Ord, Show, Read)

data Type
    = TInt
    | TBool
    | TAlgebraic TIdent
    | TPoly TPolyIdent
    | TFun Type Type
  deriving (Eq, Ord, Show, Read)

data RType = NoRetType | RetType Type
  deriving (Eq, Ord, Show, Read)

